mapper.select:
SELECT m.mb_no, m.mb_board, s.b_catename, m.mb_title, m.mb_date, m.mb_read, m.mb_del, m.m_no, e.m_id, e.m_name, ( SELECT COUNT(*) FROM multiboard m JOIN members e ON m.m_no = e.m_no
AND mb_board = #{cate}
AND m.mb_title LIKE CONCAT('%', #{searchV }, '%') AND m.mb_content LIKE CONCAT('%', #{searchV }, '%') AND e.m_name LIKE CONCAT('%', #{searchV }, '%') AND e.m_id LIKE CONCAT('%', #{searchV }, '%')
) as count FROM (multiboard m JOIN setupboard s ON m.mb_board = s.mb_cate) JOIN members e ON m.m_no = e.m_no
AND mb_board = #{cate}
AND m.mb_title LIKE CONCAT('%', #{searchV }, '%') AND m.mb_content LIKE CONCAT('%', #{searchV }, '%') AND e.m_name LIKE CONCAT('%', #{searchV }, '%') AND e.m_id LIKE CONCAT('%', #{searchV }, '%')
ORDER BY mb_no DESC
